import styled from "styled-components";

//styled-components
const TilesContainer = styled.div`
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    color: #192024;
`;

const SingleTile = styled.div`
    width: 300px;
    height: 240px;
    margin: 8px;
    border-radius: 10px;
    border: 1px solid #dce0f5;
    box-shadow: 0px 2px 10px #cfd0d6;
    display: flex;
    justify-content: center;
    align-items: center;
`;
const AirlineWraper = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;

    img {
        width: 32px;
        height: 32px;
        margin-right: 18px;
    }

    a {
        color: #5a6872;
        text-decoration: none;
    }
`;
const AirlineInfo = styled.div`
    width: 150px;
    font-size: 14px;
`;

const Name = styled.p`
    font-weight: bold;
`;

const Phone = styled.p`
    font-weight: normal;
    font-size: 14px;
`;
//Components
const Tiles = ({ data }: any) => {
    return (
        <TilesContainer>
            {data.map((e: any, index: number) => {
                return (
                    <SingleTile key={index}>
                        <AirlineWraper>
                            <img
                                src={`https://www.kayak.com${e.logoURL}`}
                                alt={`${e.name} Logo`}
                            />

                            <AirlineInfo>
                                <Name>{e.name}</Name>
                                <p>{e.alliance}</p>
                                <Phone>{e.phone}</Phone>
                                <a href={e.site} target="_blank">
                                    {e.site}
                                </a>
                            </AirlineInfo>
                        </AirlineWraper>
                    </SingleTile>
                );
            })}
        </TilesContainer>
    );
};

export default Tiles;
