import { createGlobalStyle } from "styled-components";

export default createGlobalStyle`

    :root {
        --primary: #000;
        --text: #506784;

        --font-primary: "Helvetica", system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
    }

    body {
        margin: 0;
        padding: 0;

        font-family: --font-primary ;
    }
`;
