import { useState } from "react";

import styled from "styled-components";

//styled-components
const FilterContainer = styled.div`
    margin-bottom: 56px;
    color: #192024;

    h3 {
        margin-top: 73px;
        font-size: 16px;
        font-weight: bold;
    }
`;
const Checkboxes = styled.div`
    display: flex;
    margin-top: 23px;

    label {
        display: flex;
        align-items: center;

        p {
            font-size: 14px;
            margin: 0 24px 0 10px;
        }
    }
`;
//Components
const Filter = ({ data }: any) => {
    // useState for Oneworld
    const [checkedOW, setCheckedOW] = useState(false);

    const handleChangeOW = () => {
        setCheckedOW(!checkedOW);
    };
    // useState for Sky Team
    const [checkedST, setCheckedST] = useState(false);

    const handleChangeST = () => {
        setCheckedST(!checkedST);
    };
    // useState for Star Alliance
    const [checkedSA, setCheckedSA] = useState(false);

    const handleChangeSA = () => {
        setCheckedSA(!checkedSA);
    };

    return (
        <FilterContainer>
            <h3>Filter by Alliances</h3>
            <Checkboxes>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedOW}
                        onChange={handleChangeOW}
                    />
                    <p>Oneworld</p>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedST}
                        onChange={handleChangeST}
                    />
                    <p>Sky Team</p>
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={checkedSA}
                        onChange={handleChangeSA}
                    />
                    <p>Star Alliance</p>
                </label>
            </Checkboxes>
        </FilterContainer>
    );
};

export default Filter;
