import React, { useEffect, useState } from "react";

import styled from "styled-components";
import fetchJsonp from "fetch-jsonp";

import Header from "./components/header";
import Tiles from "./components/tiles";

//styled-components
const LoadingText = styled.p`
    position: absolute;
    margin-left: 50%;
    transform: translate(-50%);
    font-size: 44px;
    font-weight: bold;
`;

//Components
function App() {
    //Object for Alliances, easy to add new one
    const alliances: any = [
        { alliance: "OW", name: "Oneworld" },
        { alliance: "ST", name: "Sky Team" },
        { alliance: "SA", name: "Star Alliance" },
    ];

    const [Loading, setLoading] = useState(true);
    const [airlinesData, setAirlinesData] = useState([]);
    const [alliance1, setAlliance] = useState([]);

    const handleChange = () => setLoading(true);
    //Fetching Data from API
    useEffect(() => {
        fetchJsonp(
            "https://kayak.com/h/mobileapis/directory/airlines/homework",
            {
                jsonpCallback: "jsonp",
            }
        )
            .then((res) => res.json())
            .then((data) => setAirlinesData(data))
            .then(() => setLoading(false));
    }, [alliance1]);

    if (Loading == true) {
        return (
            <div>
                <Header alliances={alliances} handleChange={handleChange} />
                <LoadingText>Loading...</LoadingText>
            </div>
        );
    }

    return (
        console.log(airlinesData),
        (
            <div>
                <Header alliances={alliances} handleChange={handleChange} />
                <Tiles data={airlinesData} />
            </div>
        )
    );
}

export default App;
