import { useState } from "react";

import styled from "styled-components";

//styled-components
const FilterContainer = styled.div`
    margin-bottom: 56px;
    color: #192024;

    h3 {
        margin-top: 73px;
        font-size: 16px;
        font-weight: bold;
    }
`;
const Checkboxes = styled.div`
    display: flex;
    margin-top: 23px;

    label {
        display: flex;
        align-items: center;

        p {
            font-size: 14px;
            margin: 0 24px 0 10px;
        }
    }
`;
//Components
const Filter = ({ alliances, handleChange }: any) => {
    return (
        <FilterContainer>
            <h3>Filter by Alliances</h3>
            <Checkboxes>
                {alliances.map((e: any) => {
                    return (
                        <label>
                            <input
                                type="checkbox"
                                width={20}
                                height={20}
                                value={e.alliance}
                                label={e.alliance}
                                onChange={handleChange}
                            />
                            <p>{e.name}</p>
                        </label>
                    );
                })}
            </Checkboxes>
        </FilterContainer>
    );
};

export default Filter;
