import { useState } from "react";

import styled from "styled-components";

const FilterContainer = styled.div`
    margin-bottom: 56px;
    olor: #506784;

    h3 {
        margin-top: 73px;
        font-size: 16px;
        font-weight: bold;
        c
    }
`;
const Checkboxes = styled.div`
    display: flex;

    label {
        display: flex;

        P {
        }
    }
`;

const Filter = () => {
    const [checked, setChecked] = useState(false);

    const handleChange = () => {
        setChecked(!checked);
    };

    return (
        <FilterContainer>
            <h3>Filter by Alliances</h3>
            <Checkboxes>
                <label>
                    <input
                        type="checkbox"
                        checked={checked}
                        onChange={handleChange}
                    />
                    <p>Oneworld</p>
                </label>
            </Checkboxes>
        </FilterContainer>
    );
};

export default Filter;
